import React, { useState } from 'react';
import './Meme.css';
import memesData from './memesData';

function Meme() {
    const [memeImage,setMemeImage] = useState("")

    const getMemeImage = (e)=>{
        e.preventDefault();
        const memesArray = memesData.data.memes;
        const randomNumber = Math.floor(Math.random() * memesArray.length);
        const url = memesArray[randomNumber].url;
        console.log(url);
        setMemeImage(url);
    }
    
    return (
        
        <div className="meme">
            <form action="">
                <input type="text" className="meme__inputs" placeholder="Top Text" />
                <input type="text" className="meme__inputs" placeholder="Bottom Text" />
                <button onClick={getMemeImage} className="meme__button">Get a new meme image  ðŸ–¼</button>
            </form>  
            <img className="meme__image" src={memeImage} alt="" />          
        </div>
    )
}

export default Meme
